import React, {useState, useEffect} from "react"
import {useSelector, useDispatch} from "react-redux"
import {deleteBuild} from "../actions/builderActions"
import Fade from "react-reveal"
import Button from "react-bootstrap/Button"
import {Chart} from "react-chartjs-2"

const Cart = () => {
    const [showExpand, setShowExpand] = useState(false)
    const [expandID, setExpandID] = useState("")
    const combos = useSelector(state => state.builderReducer.comboArr)
    const dispatch = useDispatch()
    // useEffect(() => {          // example of syntax if needed
    //     return () => {
    //         cleanup
    //     }
    // }, [])
    const reversedCombos = combos.map(item => item).reverse();
    const handleExpandBuild = (id) => {
        setShowExpand(!showExpand)
        setExpandID(id)
    }
    const handleShrinkBuild = () => {
        setShowExpand(!showExpand)
        setExpandID("")
    }
    return (
    <>
    <Fade top>
        <div className="container">
            <div className="row mx-0 pt-2 pb-5 justify-content-center">
                <img style={{width: "625px"}} src="img/kart-cart.png" />
            </div>
        </div>
    </Fade>
    <Fade bottom>
        <div className="container">
            {reversedCombos.map(combo => {
                return (
                    <>
                    <div className="row text-center justify-content-center">
                    {(!showExpand || expandID !== combo.id) && <>
                        <div className="col mb-2 justify-content-center">
                            <img width="96px" key={combo.driver.id} src={`img/items/${combo.driver.img}`} />
                            <img width="150px" key={combo.kart.id} src={`img/items/${combo.kart.img}`} />
                            <img width="150px" key={combo.tire.id} src={`img/items/${combo.tire.img}`} />
                            <img width="150px" key={combo.glider.id} src={`img/items/${combo.glider.img}`} />
                            <Button className="btn button-green text-white ms-5 me-2" onClick={() => handleExpandBuild(combo.id)}><img width="150px" src="/img/expand-button.png" /></Button>
                            <Button className="btn button-red" onClick={() => dispatch(deleteBuild(combo.id))}><img width="150px" src="/img/delete-button.png" /></Button>
                        </div>
                    </>}

                    {showExpand && expandID === combo.id && <>
                        <div className="col mb-2 justify-content-center">
                            <img key={combo.driver.id} src={`img/items/${combo.driver.img}`} />
                            <img key={combo.kart.id} src={`img/items/${combo.kart.img}`} />
                            <img key={combo.tire.id} src={`img/items/${combo.tire.img}`} />
                            <img key={combo.glider.id} src={`img/items/${combo.glider.img}`} />
                            <Button className="btn button-yellow text-white ms-5 me-2" onClick={() => handleShrinkBuild()}><img width="150px" src="/img/expand-button.png" /></Button>
                            <Button className="btn button-red" onClick={() => dispatch(deleteBuild(combo.id))}><img width="150px" src="/img/delete-button.png" /></Button>
                        </div>
                        <div>
                        <Chart
                    type="bar"
                    data={{
                        labels: ["Weight", "Acceleration", "On-Road Traction", "Off-Road Traction", "Mini-Turbo", "Ground Speed", "Water Speed", "Anti-Gravity Speed", "Air Speed", "Ground Handling", "Water Handling", "Anti-Gravity Handling", "Air Handling"],
                        datasets: [
                            {
                                label: "Driver",
                                data: [combo.driver.wg, combo.driver.ac, combo.driver.on, combo.driver.of, combo.driver.mt, combo.driver.sl, combo.driver.sw, combo.driver.sa, combo.driver.sg, combo.driver.tl, combo.driver.tw, combo.driver.ta, combo.driver.tg],
                                backgroundColor: [
                                    'rgba(0, 70, 240, 1)',
                                ],
                                borderColor: [
                                    'rgba(0, 70, 240, 1)',
                                ],
                                borderWidth: 1
                            },
                            {
                                label: "Body",
                                data: [kartState.wg, kartState.ac, kartState.on, kartState.of, kartState.mt, kartState.sl, kartState.sw, kartState.sa, kartState.sg, kartState.tl, kartState.tw, kartState.ta, kartState.tg],
                                backgroundColor: [
                                    'rgba(10, 191, 2, 1)',
                                ],
                                borderColor: [
                                    'rgba(10, 191, 2, 1)',
                                ],
                                borderWidth: 1
                            },
                            {
                                label: "Tire",
                                data: [tireState.wg, tireState.ac, tireState.on, tireState.of, tireState.mt, tireState.sl, tireState.sw, tireState.sa, tireState.sg, tireState.tl, tireState.tw, tireState.ta, tireState.tg],
                                backgroundColor: [
                                    'rgba(255, 245, 16, 1)',
                                ],
                                borderColor: [
                                    'rgba(255, 245, 16, 1)',
                                ],
                                borderWidth: 1
                            },
                            {
                                label: "Glider",
                                data: [gliderState.wg, gliderState.ac, gliderState.on, gliderState.of, gliderState.mt, gliderState.sl, gliderState.sw, gliderState.sa, gliderState.sg, gliderState.tl, gliderState.tw, gliderState.ta, gliderState.tg],
                                backgroundColor: [
                                    'rgba(254, 56, 55, 1)',
                                ],
                                borderColor: [
                                    'rgba(254, 56, 55, 1)',
                                ],
                                borderWidth: 1
                            }
                        ]
                    }}
                    // height={45}
                    // width={100}
                    options={{
                        // indexAxis: "y",
                        maintainAspectRatio: true,
                        aspectRatio: 1.6,
                        layout: {
                            // padding: 20
                        },
                        responsive: true,
                        plugins: {
                            legend: {
                                position: 'bottom',
                                reverse: false,
                                labels: {
                                    color: 'rgba(255, 255, 255)',
                                    boxHeight: 25,
                                    padding: 30
                                }
                            }
                        },
                        scales: {
                            x: {
                                stacked: true,
                                ticks: {
                                    color: 'rgba(255, 255, 255)'
                                }
                            },
                            y: {
                                stacked: true,
                                ticks: {
                                    color: 'rgba(255, 255, 255)'
                                }
                            }
                        }
                    }}
                />
                        </div>
                        </>}
                    </div>
                    </>
                )
            })}
        </div>
    </Fade>
    </>
    )
}

export default Cart
